<script>
export default {
  data() {
    return {
      title: 'Web advanced',
      intro: 'Welkom in deze cursus',
      fullName: 'Jelle Van Vlasselaer',
      Age: 19,
      visibility: 'display: none',
      linkUrl: 'https://qotsa.com/',
      imageUrl: 'data:image/webp;base64,',
      imgWidth: 200,
      imgHeight: 200
    }
  },
  methods: {
    dynamicText() {
      let random = Math.random();

      if (random < 0.5) {
        return 'Wij verkopen de beste producten die je op de markt kan vinden'
      }
      else {
        return 'Wie zoekt die vindt'
      }
    },
    showRandom() {
      let random = Math.random();
      return 'Het getal is: ' + random;
    },
    onImageClicked() {
      this.visibility = 'display: block'

    },
    onButtonClicked() {
      this.visibility = 'display: none'
    },
    showImage(event, path) {
      this.imageUrl = path
      event.target.innerText = 'afbeelding gewijzigd'
    }
  }
}
</script>

<template>
  <h1>Welkom in de Vue App</h1>
  <h1>{{ title }}</h1>
  <p>{{ intro }}</p>
  <a :href="linkUrl">
    <img v-bind:src="imageUrl" :height="imgHeight" :width="imgWidth">
  </a>
  <p> {{ dynamicText() }}</p>
  <h2>{{ fullName }}</h2>
  <h2>{{ Age }}</h2>
  <h2>{{ Age + 5 }}</h2>
  <p>{{ showRandom() }}</p>
  <a :href="linkUrl">{{ linkUrl }}</a><br><br>
  <input type="text" :value="fullName">
  <hr>
  <!-- Events-->
  <img v-bind:src="imageUrl" :height="imgHeight" :width="imgWidth" @click="onImageClicked()"><br>
  <Button :style="visibility" @click="onButtonClicked"> Verberg mij</Button><br>
  <Button @click="showImage($event, './src/assets/doner-kebab.jpg')">Verander Image</Button>
</template>

<style></style>